"use strict";

module.exports = function (app) {
  var Arqueo = app.db.models.Arqueo;
  app.route('/arqueo').get(function (req, res) {
    Arqueo.findAll({
      order: [['fecha', 'ASC']]
    }).then(function (result) {
      return res.json(result);
    })["catch"](function (error) {
      res.status(412).json({
        msg: error.message
      });
    });
  }).post(function (req, res) {
    Arqueo.create(req.body).then(function (result) {
      return res.json(result);
    })["catch"](function (error) {
      return res.json(error.errors[0].message);
    });
  });
  app.route('/arqueo/:id').get(function (req, res) {
    Arqueo.findOne({
      where: {
        id_arqueo: req.params.id
      }
    }).then(function (result) {
      return res.json(result);
    })["catch"](function (error) {
      res.status(404).json({
        msg: error.message
      });
    });
  }).put(function (req, res) {
    Arqueo.update(req.body, {
      where: {
        id_arqueo: req.params.id
      }
    }).then(function (result) {
      return res.sendStatus(204);
    })["catch"](function (error) {
      return res.json(error.errors[0].message);
    });
  })["delete"](function (req, res) {
    console.log(req.params);
    Arqueo.destroy({
      where: {
        id_arqueo: req.params.id
      }
    }).then(function (result) {
      return res.sendStatus(204);
    })["catch"](function (error) {
      res.status(412).json({
        msg: error.message
      });
    });
  });
};